# Initialize an empty list to store the airport data
airports = []

# Find all country divs
country_divs = soup.find_all('div', class_='airline-country')

for country_div in country_divs:
    country = country_div.get_text(strip=True)
    flag_img = country_div.find('img')
    flag_link = flag_img['src'] if flag_img else None

    # Next sibling of the country div contains the airports for that country
    airport_div = country_div.find_next_sibling('div')
    # Find all anchor tags which contain the airport data
    airport_anchors = airport_div.find_all('a', class_='airline-destination')

    for anchor in airport_anchors:
        airport_data = anchor['data-a'].split('(')
        city = airport_data[0].strip()
        code = airport_data[1].replace(')', '').strip()
        airports.append({'country': country, 'city': city, 'code': code, 'flag_link': flag_link})

# Write the data to a JSON file
with open('airports.json', 'w') as file:
    json.dump(airports, file, indent=4)

print("airports.json file created successfully.")

